<template>
    <div class="content">
        <div class="search">
            <input @input="bindKeyInput" type="text" placeholder="搜索歌曲" class="inputSearch">
            <i @click="searchI" style="background:url(data:image/svg+xml;base64,); background-size:56rpx 56rpx; width:56rpx;height:56rpx;"></i>
        </div>
        <div style="margin-top:20rpx;">
            <div v-for="item in searchData" :key="item.id" class="SingleWidth">
                <div @click="musicUrl(item.id)">
                    <img :src="item.al.picUrl" alt="">
                    <div>
                        <div style="font-size:32rpx">{{item.name}}</div>
                        <div class="searchName">
                            <p v-for="(itempeo,indexa) in item.ar" :key="indexa">
                                {{itempeo.name}} &nbsp;
                            </p>
                            <p>- {{item.al.name}}</p>
                        </div>
                    </div>
                </div>
                <i @click="JoinTheList(item.id)" style="background:url(data:image/svg+xml;base64,); background-size:40rpx 40rpx; width:40rpx;height:40rpx;"></i>
            </div>
            
        </div>
        <div class="Playing" @click="listenerButtonPause">
            <div class='wrap'>
                <i style="background:url(data:image/svg+xml;base64,); background-size:80rpx 80rpx; width:80rpx;height:80rpx;margin-top:20rpx;margin-left:20rpx"></i>
            </div>

        </div>
    </div>
</template>

<script>
var Fly = require("flyio/dist/npm/wx"); //npm引入方式
var fly = new Fly(); //创建fly实例
export default {
    data() {
        return {
            internal: false,
            searchData: {}
        };
    },
    onLoad: function(options) {
        wx.setNavigationBarTitle({
            title: "最热排行榜" //页面标题为路由参数
        });
        fly
            .get("http://localhost:3000/top/list?idx=1", {})
            .then(d => {
                this.searchData = d.data.playlist.tracks;
            })
            .catch(err => {
                console.log(err.status, err.message);
            });
    },

    mounted() {},
    methods: {
        bindKeyInput: function(e) {
            this.bindKeyInput = e.target.value;
        },
        searchI: function() {
            let searchValue = this.bindKeyInput;

            const url = "../search/main?search=" + searchValue;
            wx.navigateTo({ url });
        },
        musicUrl(e) {
            const url = "../music/main?id=" + e;
            wx.navigateTo({ url });
        },
        //监听button暂停按钮
        listenerButtonPause: function() {
            let e = wx.getStorageSync("musicId");

            if (!e) {
            } else {
                const url = "../music/main?id=" + e;
                wx.navigateTo({ url });
            }
        },
        JoinTheList: function(e) {
            let chongfu = [];
            let playing = wx.getStorageSync("playing");
            for (let index = 0; index < playing.length; index++) {
                const element = playing[index].id;
                chongfu.push(element)
            }
            for (let index = 0; index < chongfu.length; index++) {
                const elementq = chongfu[index];
                if (elementq == e) {
                    var panduanchongfu = true
                }
            }
            if (!panduanchongfu) {
                let status =  {
                    id: e,
                    status: false
                }
                playing.unshift(status);
                wx.setStorageSync("playing", playing);
            }
        }
    }
};
</script>

